export const apiKey =  'https://strangers-things.herokuapp.com/api/'
export const cohortName = '"2207-FTB-ET-WEB-PT"';

 async function fetchPosts (){
try{
    const postData= await fetch (`${apiKey}${cohortName}/posts`)
    const postResult = await postData.json()
    return postResult
}catch (error){
    throw error;
}
}
 async function createUser (username, password){
try{
    const response = await fetch (`${apiKey}${cohortName}/users/register`,{
        method: "POST",
        headers: {
            "Content-Type":"application/json"        
        },
        body:JSON.stringify({
            user:{
                username: username,
                password: password,
            },
        })
    })
    return response
}catch (error){
    throw error
}
}

export default fetchPosts
