import React, {useState } from "react";
import {  useNavigate } from "react-router-dom";
import { NewPost } from "../api/api";

import "./AddPost.css";

const AddPost= ({token, setPosts})=> {
  let navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [location, setLocation] = useState("");
  const [price, setPrice] = useState("");
  const [willDeliver, setWillDeliver] = useState(false);
  const [ errorMessage, setErrorMessage] = useState (null)

  return (<form className="ui form" onSubmit={async (event) => {
    event.preventDefault();
    const {error, post} = await AddPost(token, title, description, price, location)

    console.log('post form onSubmit:', description);

    if (post) {
        setPosts((prevPosts) => [...prevPosts, post]);
        setDescription('');
        setPrice('');
        history.push('/Posts');
    } else {
        setErrorMessage(error);
    }
}}>

        <h1>Create a New Post</h1>
          <input
          id="title"
          type="text"
          onChange={(event) => setTitle(event.target.value)}
          placeholder="title"
          ></input>
          <input
            id="description"
            onChange={(event) => setDescription(event.target.value)}
            type="text"
            placeholder="description"
            ></input>
          <input

            onChange={(event) => setLocation(event.target.value)}
            type="text"
            placeholder="location"
            ></input>
          <input type="text" placeholder="price"></input>
          <input

          type="text"
          onChange={(event) => setPrice(event.target.value)}
          placeholder="price"
          ></input>
          <input
            type="text"

            onChange={(event) => setWillDeliver(event.target.value)}
            placeholder="Will Deliver?"
            ></input>
          <button  type="Submit">
            Post
          </button>
        </form>
  );
}

export default AddPost
